import { useParams } from 'react-router-dom';
import {useState, useEffect} from 'react'
import { nanoid } from 'nanoid';

const User = ()=>{
    const {userId} = useParams()
    const[userInfo, setUserInfo] = useState(null)
    useEffect(()=>{
        fetch(`/user/${userId}`)
            .then(res => res.json())
            .then(data => {
                setUserInfo(prevUserInfo=> prevUserInfo = data)
            })
    },[])


    let info = {
        id:nanoid(),
        title: '',
        text:""
    }

    const  [newInput, setNewInput] = useState(info)

    function onChange(info) {
        setNewInput(prevNewInfo => {
            return {...prevNewInfo, [event.target.name] : event.target.value}
        })
    }

    return(
        <div>
            <h1>Hello User{userInfo !== null ? userInfo[0].name:''}</h1> 

            <form>
                <lable for="title">Title</lable>
                <input type="text" id="title" name="title" onChange={()=>onChange(newInput)}/>
                <lable for="text">Text</lable>
                <input type="text" id="text" name="text" onChange={()=>onChange(newInput)}/>
                <button type="button">Add</button>
            </form>
        </div>
     
    )
}

export default User
