import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom'
import { useState, useEffect } from "react"

const Login = ()=>{
    let info = {
        email: '',
        password: ''
    }
    const[loginInfo, setLoginInfo] = useState(info)
    const[loginSuccess, setLoginSuccess] = useState(false)

    const navigate = useNavigate()
    function handleChange(event){
        setLoginInfo(prevLoginInfo => {
            return {...prevLoginInfo, [event.target.name] : event.target.value}
        })
    }
    function handleClick(info){
        fetch('/',{
            method: 'POST',
            mode:'cors',
            headers:{"Content-Type": "application/json"},
            body: JSON.stringify(info)
        })
        .then(response => response.json())
        .then(data=> {
            console.log(data)
            setLoginSuccess(prev => prev=data)
        })
    }
    useEffect(()=>{
        console.log(`User is loged in ${loginSuccess}`)
    }, [loginSuccess])


    return (
        <form>
            <h1>Login</h1>
            <label htmlFor="email">Email</label>
            <input type="email" name="email" id="email" onChange={handleChange}/>

            <label htmlFor="password">Password</label>
            <input type="password" name="password" id="password" onChange={handleChange}/>

            <button type="button" onClick={()=>handleClick(loginInfo)}>Login</button>
            <Link to='/register'>Register</Link>
        </form>
    )
}

export default Login
